                showOrder();
            });
            function changeTab() {
                $("#reco-area").find(".mt").find("li").bind("click",
                function() {
                    $(this).siblings().removeClass("curr");
                    $(this).addClass("curr");
                    var num = $(this).index();
                    //alert(num);
                    if (num == 0) {
                        $("#reco-area").find(".wrap").empty();
                        $("#reco-area").find(".wrap").html($("#include1").html());
                        op();
                    } else if (num == 1) {
                        $("#reco-area").find(".wrap").empty();
                        $("#reco-area").find(".wrap").html($("#include2").html());
                        op();
                    }
                });
            }
            function modifyImg() {
                $("#userinfo").find(".u-icon").bind("mouseover",
                function() {
                    $("#modifyheader").show();
                });
                $("#userinfo").find(".u-icon").bind("mouseout",
                function() {
                    $("#modifyheader").hide();
                });
            }
            function showOrder() {
                $("#myOrder1").bind("mouseover",
                function() {
                    $(this).addClass("hover");
                    //$(this).css("height","100");
                    $(".sub_item").show();

                });
                $("#myOrder1").bind("mouseout",
                function() {
                    $(this).removeClass("hover");
                    $(".sub_item").hide();
                    //$(this).css("height","20");
                });
            }

            //发送手机验证码
            function tel_send(tel) {
                tel = tel.replace(/(^\s*)|(\s*$)/g, '');
                window.location.href = "http://" + window.location.host + "/main.php?do=user_reg_phone&sub_flag=4&tel=" + tel;
            }

            function changeCity(city_code) {
                var b = new Base64();
                city_code = city_code.toString();
                city_dode = b.encode(city_code);
                var Days = 7;
                var exp = new Date();
                exp.setTime(exp.getTime() + Days * 24 * 60 * 60 * 1000);
                document.cookie = 'tuniuuser_citycode' + "=" + escape(city_dode) + ";expires=" + exp.toGMTString() + ";path=/;domain=.tuniu.com";
                thisURL = window.location.pathname;
                window.location.href = thisURL;
            }</script>
